import forms from "./formEntrance.json";

export function getName() {
  return forms.map((form) => {
    return (
      <form>
        <label for={form.id}> {form.name} </label>
        <br></br>

          {form.type === 'select' ? (
            <select>
                <option></option>
            </select>
          ) : ( <input
            type={form.type}
            placeholder={form.placeholder}
            id={form.id}
            name={form.name}
            />)

          }
      </form>

    );
  });
}
function getCities(items){
    return items.options.map((item)=>{
        return(
            <option label={item.lable}></option>
        )
    })
}

function getGender(indicators){
    return indicators.options.map((index)=>{
        return(
            <option label={index.label}></option>
        )
    })
}

function doText(){
    return forms.map((form) =>{
        {if(form.type ==='type'){
            return getName();
        }
        }
    })
}

function doSelects(){
    return forms.map((form)=>{
        {if(form.type==='select'){
            return(
                 <form>
                    {getCities()}
                 </form>
            )
        }
        }
    })
}

function doRadios(){
    return forms.map((form)=>{
        {if(form.type==='radio'){
            return(
                <form>
                    {getGender()}
                </form>
            )
        }}
    })
}
