// import logo from "./logo.svg";
import "./App.css";
import data from "./users.json";

function userGender() {
  return (
    <div>
      {data.map((user) => {
        if ((user.gender).toLowerCase() == "Male") {
          return (
            <div className="box">
              <p className="blue">
                {user.first_name} {user.last_name} ({user.gender})
              </p>
            </div>
          );
        } else if (user.gender == "Female") {
          return (
            <div className="box">
              <p className="red">
                {user.first_name} {user.last_name} ({user.gender})
              </p>
            </div>
          );
        }
      })}
    </div>
  );
}

export default userGender;
