// import logo from "./logo.svg";
import "./App.css";
import data from "./users.json";

function userGender() {
  return (
    <div>
      {data.map((user) => {
        return (
          <div className="box">
            if((user.gender).toLowerCase() === "male")
            {
              <p className="blue">
                {user.first_name} {user.last_name} ({user.gender})
              </p>
            }
            else
            {
              <p className="red">
                {user.first_name} {user.last_name} ({user.gender})
              </p>
            }
          </div>
        );
      })}
    </div>
  );
}

export default userGender;
