// import logo from "./logo.svg";
import "./App.css";
import data from "./users.json";

function userGender() {
  return (
    <div>
      {data.map((user) => {
        if (user.gender == "Male") {
          return <p className="blue">{user.first_name} {user.last_name} {user.gender}</p>;
        } else if(user.gender=='Female'){
            return <p className="red">{user.first_name} {user.last_name} {user.gender}</p>
        }
      })}
    </div>
  );
}

export default userGender;
