// import logo from "./logo.svg";
import "./App.css";
import data from "./users.json";

let isTrue = true
function userGender() {
  return (
    <div className="d-flex">
      {data.map((user) => {
        if ((user.gender).toLowerCase() === "male") {
          return (
            <div className="box">
              <p className="blue">
                {user.first_name} {user.last_name} ({user.gender})
              </p>
            </div>
          );
        } else if ((user.gender).toLowerCase() === "female") {
          return (
            <div className="box">
              <p className="red">
                {user.first_name} {user.last_name} ({user.gender})
              </p>
            </div>
          );
        }
        /* ------------------- */

        // return (
